// --- FULFILLMENT ENDPOINT ---
app.post('/fulfill-order', async (req, res) => {
    const { order_id, pnr } = req.body;
    if (!order_id || !pnr) {
        return res.status(400).json({ error: "Missing order_id or pnr" });
    }
    try {
        const params = new URLSearchParams();
        params.append('fulfillment_status', 'SUCCESS');
        params.append('fulfillment_command', 'NO_ACTION');
        params.append('fulfillment_time', new Date().toISOString());
        params.append('fulfillment_id', pnr);

        console.log(`Initiating Fulfillment for Order: ${order_id} | PNR: ${pnr}`);

        const response = await axios.post(
            `https://sandbox.juspay.in/orders/${order_id}/fulfillment`,
            params,
            { headers: { 'Authorization': JUSPAY_AUTH, 'Content-Type': 'application/x-www-form-urlencoded' } }
        );
        res.json({ success: true, data: response.data });
    } catch (error) {
        console.error(" Fulfillment Failed:", error.response?.data || error.message);
        res.status(500).json({ error: error.message });
    }
});

